mapper.select:
SELECT A.id,A.name, A.money,A.number,A.money*A.number AS totalMoney,A.delete_flag deleteFlag, A.remark,A.year_mos_day yearMosDay,su.name userName,d.name dataDictionaryName FROM financial_accounting_b A LEFT JOIN (SELECT userid,name FROM sys_user) su ON su.userid=A.userid LEFT JOIN (SELECT id,name FROM data_dictionary WHERE type='finance') d ON d.id=A.data_dictionary_id
AND A.type=#{type}
AND A.data_dictionary_id IN
#{item}
AND DATE_FORMAT( A.year_mos_day,'%Y-%m') = #{yearMosDay}
ORDER BY A.create_date DESC

SELECT SUM(CASE WHEN zz.bill_name = '租金' THEN zz.money * zz.number ELSE '0' END) AS rent, SUM(CASE WHEN zz.bill_name = '人员费用' THEN zz.money * zz.number ELSE '0' END) AS staffCosts, SUM(CASE WHEN zz.bill_name = '物流费用' THEN zz.money * zz.number ELSE '0' END) AS logisticsFees, SUM(CASE WHEN zz.bill_name = '常态杂物' THEN zz.money * zz.number ELSE '0' END) AS normalcyIncidentals, SUM(CASE WHEN zz.bill_name = '报销费用' THEN zz.money * zz.number ELSE '0' END) AS claimExpense, SUM(CASE WHEN zz.bill_name = '杂费' THEN zz.money * zz.number ELSE '0' END) AS incidentals, SUM(CASE WHEN zz.bill_name = '其他' THEN zz.money * zz.number ELSE '0' END) AS otherExpenses FROM(SELECT A.bill_name,B.money,B.number FROM financial_accounting_b B LEFT JOIN financial_accounting A ON B.financial_accounting_id=A.id WHERE B.year_mos_day LIKE "%"#{yearmos}"%"
AND B.sys_area_id=#{sysAreaid}
) AS zz
